)
RETURNS VARGRAPHIC(256) CCSID 13488
LANGUAGE SQL
SPECIFIC [SCLIBRARY].CST_GetPrimaryElectAddressFnc
DISALLOW PARALLEL
NOT DETERMINISTIC
CALLED ON NULL INPUT
BEGIN
	
	DECLARE ElectronicAddress	VARGRAPHIC(256) CCSID 13488;

	SELECT	EAEMAL
