CREATE OR REPLACE Procedure [SCLIBRARY].COM_GetSalesOrderHeader
(
	IN Company varchar(5),
	IN OrderType varchar(3),
	IN OrderNumber NUMERIC(15,0),
	IN InstallationID varchar(3)
)

