LANGUAGE SQL 
SPECIFIC [SCLIBRARY].POS_GetDrawer 
NOT DETERMINISTIC 
MODIFIES SQL DATA 
CALLED ON NULL INPUT 
BEGIN 

	DECLARE TMP_DRAWERCUR1 CURSOR WITH HOLD WITH RETURN TO CLIENT FOR 
	SELECT 
		DR$9REGID AS RegisterID,
		DR$9DRAW  AS DrawerNumber,
		DR$9DRST  AS Status,
		DR$9DRMUL AS AllowMultipleUsers,
