-- #param Constant					Constant ID
-- #param InstallationID			Installation ID
-- #param ConstantValue				Constant Value

CREATE OR REPLACE Procedure [SCLIBRARY].CMM_UpdConstant
(	
	IN Constant		    varchar(10),
	IN InstallationID   varchar(3),
