			A.IMUOM4 AS	PRICINGUNITOFMEASURE , 
			A.IMSRTX AS	SEARCHITEMTEXT , 
			A.IMSTKT AS	STOCKINGTYPE , 
			A.IMLNTY AS	LINETYPE , 
			INVENTORYFLAGVALUE AS INVENTORYFLAG , 
			A.IMIFLA AS	ITEMFLASHMESSAGE,
			A.IMTMPL AS MATRIXTEMPLATE,
			A.IMMIC AS MATRIXCONTROLLED,
			A.IMCYCL AS CYCL,
			A.IMPRP0 AS PRP0,
			A.IMPRP1 AS PRP1,
			A.IMPRP2 AS PRP2,
			A.IMPRP3 AS PRP3,
			A.IMPRP4 AS PRP4,
			A.IMPRP5 AS PRP5,
			A.IMPRP6 AS PRP6,
			A.IMPRP7 AS PRP7,
			A.IMPRP8 AS PRP8,
			A.IMPRP9 AS PRP9,
			A.IMSHCM AS SHCM,
			A.IMSHCN AS SHCN,
			A.IMSRP0 AS SRP0,
			A.IMSRP1 AS SRP1,
			A.IMSRP2 AS SRP2,
			A.IMSRP3 AS SRP3,
			A.IMSRP4 AS SRP4,
			A.IMSRP5 AS SRP5,
			A.IMSRP6 AS SRP6,
			A.IMSRP7 AS SRP7,
			A.IMSRP8 AS SRP8,
			A.IMSRP9 AS SRP9
		FROM 
			[SCDATA].F4101 A					 /*ITEM MASTER */
		WHERE 
			A.IMITM = SHORTITEMNUMBER 
		FETCH FIRST 1 ROW ONLY;
	ELSE
		INSERT INTO SESSION.ITEMTBL 
		SELECT 
			E.BI$9INID AS	INSTALLATIONID , 
			B.IBMCU	AS	BRANCHPLANT , 
			D1.MCDL01 AS	BRANCHPLANTDESC , 
			A.IMITM	AS	ITEMNUMBER , 
			A.IMLITM AS	LONGITEMNUMBER , 
			A.IMAITM AS	AUXITEMNUMBER , 
			A.IMDSC1 , 
			A.IMDSC2 , 
			A.IMUOM1 AS	PRIMARYUNITOFMEASURE , 
			A.IMUOM2 AS	SECONDARYUNITOFMEASURE , 
			A.IMUOM4 AS	PRICINGUNITOFMEASURE , 
			A.IMSRTX AS	SEARCHITEMTEXT , 
			A.IMSTKT AS	STOCKINGTYPE , 
			A.IMLNTY AS	LINETYPE , 
			INVENTORYFLAGVALUE AS INVENTORYFLAG , 
			A.IMIFLA AS	ITEMFLASHMESSAGE,
			A.IMTMPL AS MATRIXTEMPLATE,
			A.IMMIC AS MATRIXCONTROLLED,
			A.IMCYCL AS CYCL,
			A.IMPRP0 AS PRP0,
			A.IMPRP1 AS PRP1,
			A.IMPRP2 AS PRP2,
			A.IMPRP3 AS PRP3,
			A.IMPRP4 AS PRP4,
			A.IMPRP5 AS PRP5,
			A.IMPRP6 AS PRP6,
			A.IMPRP7 AS PRP7,
			A.IMPRP8 AS PRP8,
			A.IMPRP9 AS PRP9,
			A.IMSHCM AS SHCM,
			A.IMSHCN AS SHCN,
			A.IMSRP0 AS SRP0,
			A.IMSRP1 AS SRP1,
			A.IMSRP2 AS SRP2,
			A.IMSRP3 AS SRP3,
			A.IMSRP4 AS SRP4,
			A.IMSRP5 AS SRP5,
			A.IMSRP6 AS SRP6,
			A.IMSRP7 AS SRP7,
			A.IMSRP8 AS SRP8,
			A.IMSRP9 AS SRP9
		FROM 
			[SCDATA].F4101 A					 /*ITEM MASTER */
			INNER JOIN [SCDATA].FQ679910 E		 /* BRACHPLANT BY INSTALLATION */
			ON E.BI$9INID = INSTALLATIONID 
			JOIN [SCDATA].F4102 B				 /*ITEM MASTER BRANCH */
			ON B.IBITM = A.IMITM 
			AND B.IBMCU = E.BIMCU 
			JOIN [SCDATA].F0006 D1				 /*BRANCH PLANT MASTER */
			ON B.IBMCU = D1.MCMCU 
		WHERE 
			A.IMITM = SHORTITEMNUMBER AND	 
			( BRANCHPLANT = '*' OR B.IBMCU = BRANCHPLANT ) 
		FETCH FIRST 1 ROW ONLY ; 
	END IF ; 
		
	RETURN 
	( 
		SELECT 
			TMP.INSTALLATIONID , 
			TMP.BRANCHPLANT , 
			TMP.BRANCHPLANTDESC , 
			TMP.ITEMNUMBER , 
			TMP.LONGITEMNUMBER , 
			TMP.AUXITEMNUMBER , 
			CASE InformationFlag 
				WHEN 0 THEN TMP.DESCRIPTION1 
				ELSE COALESCE ( ContenDesc.DESC1, TMP.DESCRIPTION1 ) 
			END AS Description1 , 
			CASE INFORMATIONFLAG 
				WHEN 0 THEN TMP.DESCRIPTION2 
				ELSE COALESCE ( ContenDesc.DESC2, TMP.DESCRIPTION2 ) 
			END AS Description2 , 
			CASE InformationFlag 
				WHEN 0 THEN '' 
				ELSE ContenDesc.URL 
			END AS URL , 
			TMP.PRIMARYUNITOFMEASURE , 
			TMP.SECONDARYUNITOFMEASURE , 
			TMP.PRICINGUNITOFMEASURE , 
			TMP.SEARCHITEMTEXT , 
			TMP.STOCKINGTYPE , 
			TMP.LINETYPE , 
			TMP.INVENTORYFLAG , 
			TMP.ITEMFLASHMESSAGE , 
			TMP.MATRIXTEMPLATE,
			TMP.MATRIXCONTROLLED,
			MatrixParentItemNumber AS MatrixParentItemNumber,
			0  AS	AddressNumber,
			'' AS	AllowPickup,
			TMP.CYCL,
			TMP.PRP0,
			TMP.PRP1,
			TMP.PRP2,
			TMP.PRP3,
			TMP.PRP4,
			TMP.PRP5,
			TMP.PRP6,
			TMP.PRP7,
			TMP.PRP8,
			TMP.PRP9,
			TMP.SHCM,
			TMP.SHCN,
			TMP.SRP0,
			TMP.SRP1,
			TMP.SRP2,
			TMP.SRP3,
			TMP.SRP4,
			TMP.SRP5,
			TMP.SRP6,
			TMP.SRP7,
			TMP.SRP8,
			TMP.SRP9	 	 
		FROM SESSION.ITEMTBL TMP
		LEFT OUTER JOIN TABLE ( [SCLIBRARY].INV_GetItemContenDescAndURLFnc ( InstallationID , TMP.ITEMNUMBER, LangPref , DesignStatus ) ) AS ContenDesc
			ON ContenDesc.DESCITM = TMP.ITEMNUMBER); /* END SELECT RETURN SECTION */
	 
	END;
END ;
