			''				AS InventoryFlag, 
			A.IMIFLA		AS ItemFlashMessage,
			A.IMTMPL		AS MatrixTemplate,
			A.IMMIC			AS MatrixControlled,
			0				AS MatrixParentItemNumber,
			0   AS  AddressNumber,
			''	AS	AllowPickup,
			IMCYCL AS CYCL,
			IMPRP0 AS PRP0,
			IMPRP1 AS PRP1,
			IMPRP2 AS PRP2,
			IMPRP3 AS PRP3,
			IMPRP4 AS PRP4,
			IMPRP5 AS PRP5,
			IMPRP6 AS PRP6,
			IMPRP7 AS PRP7,
			IMPRP8 AS PRP8,
			IMPRP9 AS PRP9,
			IMSHCM AS SHCM,
			IMSHCN AS SHCN,
			IMSRP0 AS SRP0,
			IMSRP1 AS SRP1,
