              [SCDATA].FQ67994E B
              ON A.PE$9PCD = B.PE$9PCD 
              and B.PE$9INID = InstallationID
              and (IndividualCompanyFlag IS NULL OR B.PE$9ICF = IndividualCompanyFlag)
	WHERE 
			PECLS='ECOM';
		

	OPEN TEMP_CURSOR1;
END




