-- #desc					Add extended information to regular order.
-- #bl_class				Premier.Commerce.SalesOrderAddExtendedInfoCommand.cs
-- #db_dependencies			N/A
-- #db_references			N/A	

-- #param OrderNumber		Order Number
-- #param OrderCompany		Order Company
-- #param OrderType			Order Type

CREATE OR REPLACE Procedure [SCLIBRARY].COM_AddOrderExtendedInfo
(
	IN	OrderNumber		NUMERIC(15,0),
	IN	OrderCompany	VARCHAR(5),
	IN	OrderType		VARCHAR(2)
)

DYNAMIC RESULT SETS 1 
LANGUAGE SQL 
SPECIFIC [SCLIBRARY].COM_AddOrderExtendedInfo 
NOT DETERMINISTIC 
MODIFIES SQL DATA 
CALLED ON NULL INPUT 
BEGIN 
	BEGIN			
		DECLARE IsFreight INT DEFAULT 0;
		
		SET IsFreight = (SELECT COUNT(*) FROM [SCDATA].FQ67496 WHERE FRKCOO = OrderCompany AND FRDCTO = OrderType AND FRDOCO = OrderNumber);

		IF(IsFreight = 0) THEN /* Freight OFF */
			
			/*HEADER*/
			IF(NOT EXISTS (SELECT * FROM [SCDATA].FQ674201 WHERE SHKCOO = OrderCompany AND SHDOCO = OrderNumber AND SHDCTO = OrderType))THEN
				INSERT INTO [SCDATA].FQ674201
				(
					SHKCOO,
					SHDOCO,
					SHDCTO,
					SH$9AN8,
					SHIDLN,
					SH$9TYP,
					SH$9SHAN,
					SHRCK7,
					SH$9WHI,
					SH$9MKT,
					SH$9MMID,
					SH$9PD,
					SH$9DT,
					SHPID,
					SHJOBN,
					SHUSER,
					SHUPMJ,
					SHUPMT
				)
				SELECT      
					SHKCOO	AS SHKCOO,
					SHDOCO	AS SHDOCO,
					SHDCTO	AS SHDCTO,
					SHAN8	AS SH$9AN8,
					0		AS SHIDLN,
					0		AS SH$9TYP,
					SHSHAN	AS SH$9SHAN,
					0		AS SHRCK7,
					' '		AS SH$9WHI,
					' '		AS SH$9MKT,
					0		AS SH$9MMID,
					0		AS SH$9PD,
					0		AS SH$9DT,
					SHPID	AS SHPID,
					SHJOBN	AS SHJOBN,
					SHUSER	AS SHUSER,
					SHUPMJ	AS SHUPMJ,
					SHTDAY	AS SHUPMT
				FROM [SCDATA].F4201
				WHERE SHDOCO = OrderNumber AND 
					  SHKCOO = OrderCompany AND 
					  SHDCTO = OrderType;
			END IF;
			
			/*DETAIL*/
			INSERT INTO [SCDATA].FQ674211
			(
				SDKCOO,
				SDDOCO,
				SDDCTO,
				SDLNID,
				SD$9AN8,
				SDIDLN,
				SD$9TYP,
				SD$9SHAN,
				SDRCK7,
				SD$9MKTG,
				SD$9IGPR,
				SDKIT,
				SDMMCU,
				SDCPNT,
				SDKTLN,
				SD$9SLN,
				SD$9FQTY,
				SD$9IID,
				SD$9STS,
				SDQNTY,
				SD$9UOM,
				SDPID,
				SDJOBN,
				SDUSER,
				SDUPMJ,
				SDUPMT
			)
			SELECT
				A.SDKCOO AS SDKCOO,
				A.SDDOCO AS SDDOCO,
				A.SDDCTO AS SDDCTO,
				A.SDLNID AS SDLNID,
				A.SDAN8  AS SD$9AN8,
				0   AS SDIDLN,
				0   AS SD$9TYP,
				A.SDSHAN AS SD$9SHAN,
				0   AS SDRCK7,
				' '   AS SD$9MKTG,
				0   AS SD$9IGPR,
				0   AS SDKIT,
				' '   AS SDMMCU,
				0   AS SDCPNT,
				0   AS SDKTLN,
				0   AS SD$9SLN,
				0   AS SD$9FQTY,
				0   AS SD$9IID,
				'A'   AS SD$9STS,
				0   AS SDQNTY,
				' '   AS SD$9UOM,
				A.SDPID  AS SDPID,
				A.SDJOBN AS SDJOBN,
				A.SDUSER AS SDUSER,
				A.SDUPMJ AS SDUPMJ,
				A.SDTDAY AS SDUPMT
			FROM [SCDATA].F4211 A
			WHERE A.SDDOCO = OrderNumber AND 
				   A.SDKCOO = OrderCompany AND 
				   A.SDDCTO = OrderType AND 
				   NOT EXISTS (SELECT * FROM [SCDATA].FQ674211 B 
								WHERE B.SDKCOO = OrderCompany AND B.SDDOCO = OrderNumber AND B.SDDCTO = OrderType AND B.SDLNID = A.SDLNID);		
		END IF;
	END;
END  ;
