	BEGIN
		DECLARE TEMP_CURSOR1 CURSOR WITH RETURN TO CLIENT FOR V_DYNAMIC ;
		
		SET WHERE_DYNAMIC = N'';
		SET INNER_DYNAMIC = N'';
		SET DISPLAYITEM_DYNAMIC = N'';
		SET FROM_DYNAMIC = N'';
		SET LEFT_DYNAMIC = N'';
