		IF (SOrderRestric = N'1' ) THEN
			SET INNER_DYNAMIC = INNER_DYNAMIC || N' INNER JOIN [SCDATA].FQ67008 DR										
										ON  DR.DR$9INID = N'''||SOrderINID||'''	
										AND DR.DR$9CNST = N''SO_RESTRIC''
										AND DR.DRKY  = A.OrderType ';
