CREATE OR REPLACE PROCEDURE [SCLIBRARY].QTS_GetQuoteOrderInfo_Z
(
	IN InstallationID GRAPHIC(3) CCSID 13488 , 
	IN OrderCompany GRAPHIC(5) CCSID 13488 , 
	IN OrderType GRAPHIC(2) CCSID 13488 , 
	IN QuoteNumber NUMERIC(8, 0) ,
