		WHERE
			INBU.UI$9INID = InstallationID
			AND WUSR.WA$9WAN8 = WebAccountId
			FOR FETCH ONLY;
				
	DECLARE TEMP_CURSOR2 CURSOR WITH HOLD WITH RETURN TO CLIENT FOR	
