CREATE PROCEDURE [DBO].INV_ExcGetCatDetailTemplate
(
	@CatalogID			NVARCHAR(3),
	@UniqueKeyID		FLOAT,
	@TemplateID			NVARCHAR(10) = NULL OUTPUT
)
AS

DECLARE @Exist FLOAT 
DECLARE @ParentTmp FLOAT
DECLARE @TemplateIDTemp NVARCHAR(10)
