	   FROM [SCDATA].F004201 AS A 
		LEFT OUTER JOIN [SCDATA].FQ670041 AS B 
		 ON B.PTDOCO = A.PTDOCO
		AND B.PTDCTO = A.PTDCTO
		AND B.PTKCOO = A.PTKCOO
		AND B.PTLNID = A.PTLNID
		AND B.PTSFXO = A.PTSFXO
		AND B.PTSEQ = A.PTSEQ
		AND B.PTPOF = A.PTPOF
