	DELETE FROM [SCDATA].FQ674214 WHERE EXISTS(SELECT WA$9WAN8 FROM [SCDATA].FQ67101
													INNER JOIN [SCDATA].FQ67996
														ON WA$9USER = UI$9USER
													WHERE WA$9WAN8 = WH$9WAN8
