								CHPGTX,
								CHDL01,
								CHDL02,
								CHDL03,
								CHDL04,
								CHDL05,
								CHDL06,
								CHDL07,
								CHDL08,
								CHDL09,
								CHDL10,
								CHCOMMENTS,
								CH$9PRI,
								CH$9HTML,
								CHPTURL,
								CHURCD,
								CHURDT,
								CHURAT,
								CHURAB,
								CHURRF,
