-- #param @AddressFormatId	Output value

CREATE  PROCEDURE [DBO].CMM_ExcGetCountryAddrFormat
  @Country			NVARCHAR(3),
  @AddressFormatId	NVARCHAR(2) OUTPUT
AS

	SET NOCOUNT ON
  
	SET @AddressFormatId = (SELECT A.CKADID 
						FROM [SCDATA].F0070 A
						WHERE A.CKCTR  = @Country)
	
	IF(@AddressFormatId IS NULL)
