	AND	A.DRRT = @UserDefinedCode
	AND	SUBSTRING(A.DRKY, 10 - @CodeLength + 1, @CodeLength) NOT IN (SELECT ISNULL(DRKY,'')
							  FROM	 [SCDATA].FQ67008 DR
							  WHERE  DR$9INID = @InstallationID
							  AND    DRSY	  = @ProductCode
