	DECLARE @ItemNumber NVARCHAR(25);

	DECLARE @SQL_DYNAMIC NVARCHAR(MAX) = '';
	DECLARE @DeleteItemList NVARCHAR(MAX);
	DECLARE @ProductNumberQuery NVARCHAR(MAX);

	SET @ProductNumberQuery = N' '
	IF @ProductListXML IS NOT NULL 	
