	@SourceInstallationID NVARCHAR(3),
	@SourceCatalogID NVARCHAR(3),
	@NewInstallationID NVARCHAR(3),
	@NewCatalogID NVARCHAR(3)
	
AS
BEGIN
	--CATALOG COPY
	INSERT INTO [SCDATA].FQ67412 (CA$9CLGID, CA$9EFM, CAEFFF, CAEFFT, CADSC1, CASEQ, CA$9INID)
	SELECT 
		@NewCatalogID,
		CA$9EFM,
