		WHERE 
			IVAN8 = @AddressNumber
			AND IVXRT = @CustCrossRefType
			AND (IVCITM = @RefProductNumber)
			AND (@EffectiveOnly = 0 OR (@CurrentJulianDate>= IVEFTJ AND @CurrentJulianDate <= IVEXDJ))
	END


