-- #desc				Gets list of Matrix segment values for an specific segment
-- #bl_class			Premier.Inventory.MatrixAttributeValueList.cs
-- #db_dependencies		N/A
-- #db_references		N/A

CREATE Procedure [DBO].INV_GetMatrixAttrValueList
(
	@Template		NCHAR(20),
	@Style			NCHAR(10),
	@SegmentNumber	INT
)
AS
BEGIN

	DECLARE @VerticalSegment AS INT;
	DECLARE @HorizontalSegment AS INT;

	SET @VerticalSegment = 0;
	SET @HorizontalSegment = 0;

	/* Retrieve horizontal and vertical segment numbers */
	SELECT @HorizontalSegment = MTM.T7HSEG,			
		   @VerticalSegment= (CASE 
								WHEN MTM.T7HSEG =  MTM.T7NSEG THEN MTM.T7NSEG - 1 
								ELSE MTM.T7NSEG 
							 END)   			
		FROM [SCDATA].F410014 MTM
		WHERE MTM.T7TMPL= @Template;

	IF(@Style = '*')
	BEGIN
		;WITH CTE AS (
			SELECT
				S0TMPL AS Template,
				CASE @SegmentNumber
					WHEN 2 THEN S0SEG2
					WHEN 3 THEN S0SEG3
					WHEN 4 THEN S0SEG4
					WHEN 5 THEN S0SEG5
					WHEN 6 THEN S0SEG6
					WHEN 7 THEN S0SEG7
					WHEN 8 THEN S0SEG8
					WHEN 9 THEN S0SEG9
					WHEN 0 THEN S0SEG0
					END AS SegmentValue,
				CASE @SegmentNumber
					WHEN 2 THEN S0SGD2
					WHEN 3 THEN S0SGD3
					WHEN 4 THEN S0SGD4
					WHEN 5 THEN S0SGD5
					WHEN 6 THEN S0SGD6
					WHEN 7 THEN S0SGD7
					WHEN 8 THEN S0SGD8
					WHEN 9 THEN S0SGD9
					WHEN 0 THEN S0SGD0
					END AS SegmentDescription,
				CASE 
					WHEN @SegmentNumber = @VerticalSegment THEN S0MRNO
					WHEN @SegmentNumber = @HorizontalSegment THEN S0MCNO
					ELSE 0
					END AS SegmentSequence
			FROM [SCDATA].F41008
			WHERE  S0TMPL = @Template
		)
		/* Distinct without SegmentDescription to avoid same code with different description */
		SELECT Template,
				@Style	AS Style,
				@SegmentNumber AS SegmentNumber, 
				SegmentValue, 
				MAX(B.SegmentDescription) AS SegmentDescription,
				MIN(B.SegmentSequence) AS SegmentSequence
		FROM CTE B
		WHERE SegmentValue <> ''
		GROUP BY B.Template, B.SegmentValue;
	END
	ELSE
	BEGIN
		;WITH CTE AS (
			SELECT
				S0TMPL AS Template,
				CASE @SegmentNumber
					WHEN 2 THEN S0SEG2
					WHEN 3 THEN S0SEG3
					WHEN 4 THEN S0SEG4
					WHEN 5 THEN S0SEG5
					WHEN 6 THEN S0SEG6
					WHEN 7 THEN S0SEG7
					WHEN 8 THEN S0SEG8
					WHEN 9 THEN S0SEG9
					WHEN 0 THEN S0SEG0
					END AS SegmentValue,
				CASE @SegmentNumber
					WHEN 2 THEN S0SGD2
					WHEN 3 THEN S0SGD3
					WHEN 4 THEN S0SGD4
					WHEN 5 THEN S0SGD5
					WHEN 6 THEN S0SGD6
					WHEN 7 THEN S0SGD7
					WHEN 8 THEN S0SGD8
					WHEN 9 THEN S0SGD9
					WHEN 0 THEN S0SGD0
					END AS SegmentDescription,
				CASE 
					WHEN @SegmentNumber = @VerticalSegment THEN S0MRNO 
					WHEN @SegmentNumber = @HorizontalSegment THEN S0MCNO
					ELSE 0
					END AS SegmentSequence
			FROM [SCDATA].F41008
			WHERE  S0TMPL = @Template AND S0SEG1 = @Style
		)/* Distinct without Description to avoid same code with different description */
		SELECT Template,
				@Style	AS Style,
				@SegmentNumber AS SegmentNumber, 
				SegmentValue, 
				MAX(B.SegmentDescription) AS SegmentDescription,
				MIN(B.SegmentSequence) AS SegmentSequence
		FROM CTE B
		WHERE SegmentValue <> ''
		GROUP BY B.Template, B.SegmentValue;
	END
END
GO
