		0					AS AddressNumber,
		''					AS AllowPickup,
		IMCYCL AS CYCL,
		IMPRP0 AS PRP0,
		IMPRP1 AS PRP1,
		IMPRP2 AS PRP2,
		IMPRP3 AS PRP3,
		IMPRP4 AS PRP4,
		IMPRP5 AS PRP5,
		IMPRP6 AS PRP6,
		IMPRP7 AS PRP7,
		IMPRP8 AS PRP8,
		IMPRP9 AS PRP9,
		IMSHCM AS SHCM,
		IMSHCN AS SHCN,
		IMSRP0 AS SRP0,
		IMSRP1 AS SRP1,
		IMSRP2 AS SRP2,
		IMSRP3 AS SRP3,
		IMSRP4 AS SRP4,
		IMSRP5 AS SRP5,
		IMSRP6 AS SRP6,
		IMSRP7 AS SRP7,
		IMSRP8 AS SRP8,
		IMSRP9 AS SRP9
	FROM 
