				A.AuxiliaryProductNumber,
				A.Description1,
				A.Description2,
				A.StockingType,
				A.InventoryFlag,
				A.LineType,
				A.PrimaryUom,
				A.PricingUom,
				A.ShippingUom,
				A.AbcCodeSales,
				A.AbcCodeMargin,
				A.AbcCodeInvestment,
				A.UpcNumber,
				A.ScType,
				A.MatrixTemplate,
				A.MatrixControlled,
				A.Segment1,
				A.CycleCountCategory, 
				A.CategoryCode10, 
				A.CategoryCode1, 
				A.CategoryCode2, 
				A.CategoryCode3, 
				A.CategoryCode4, 
				A.CategoryCode5, 
				A.CategoryCode6, 
