				A.IMMIC,
				A.IMSEG1,
				A.IMCYCL, 
				A.IMPRP0, 
				A.IMPRP1, 
				A.IMPRP2, 
				A.IMPRP3, 
				A.IMPRP4, 
				A.IMPRP5, 
				A.IMPRP6, 
				A.IMPRP7, 
				A.IMPRP8, 
				A.IMPRP9, 
				A.IMSHCM, 
				A.IMSHCN, 
				A.IMSRP0, 
				A.IMSRP1, 
				A.IMSRP2, 
				A.IMSRP3, 
				A.IMSRP4, 
				A.IMSRP5, 
				A.IMSRP6, 
				A.IMSRP7, 
				A.IMSRP8, 
				A.IMSRP9,
				A.IMSRTX,
				A.IMTFLA
			FROM 
				[SCDATA].F4101 A
			INNER JOIN [SCDATA].F40205 C
				ON C.LFLNTY = A.IMLNTY
			INNER JOIN [SCDATA].FQ679910 E
