							A.IMUOM4 ,
							A.IMUOM6 ,
							A.IMABCS ,
							A.IMABCM ,
							A.IMABCI ,
							A.IMUPCN ,
							A.IMTMPL ,
							A.IMMIC	,
							A.IMSEG1 ,
							A.IMCYCL , 
							A.IMPRP0 , 
							A.IMPRP1 , 
							A.IMPRP2 , 
							A.IMPRP3 , 
							A.IMPRP4 , 
							A.IMPRP5 , 
							A.IMPRP6 , 
							A.IMPRP7 , 
