							A.IMSRP2 , 
							A.IMSRP3 , 
							A.IMSRP4 , 
							A.IMSRP5 , 
							A.IMSRP6 , 
							A.IMSRP7 , 
							A.IMSRP8 , 
							A.IMSRP9,
							A.IMSRTX,
							A.IMTFLA
			) ';
		END;

		IF(@WhereCondition IS NOT NULL AND @WhereCondition <> '*')
