				SET @WHERE_DYNAMIC = N' WHERE ' + @WhereCondition;
			END

		IF(@LongProductNumbersXML IS NOT NULL)
			BEGIN				
				SET @SQL_DYNAMIC = @SQL_DYNAMIC + @INSERT_DYNAMIC + N'
