	@ShortProductNumbersXML	XML 

AS	
BEGIN

	DECLARE @CurrentJulianDate NUMERIC
    SET @CurrentJulianDate = [DBO].CMM_GetCurrentJulianDate (GETDATE())

	;WITH CTE AS 
		(
