	AND (@UnitOfMeasure = '*' OR SUBSTRING (A.DRKY,10-@CODELENGTH+1 ,@CODELENGTH) = @UnitOfMeasure) 
	AND	(@Description = '*' OR A.DRDL01 LIKE '%' + @Description + '%') 					
	ORDER BY A.DRKY;

