		END
	
	SET @P4210Version=''
	
	/* Insert into variables (PricingGroup -  AdjustmentSchedule - State - Country - BusinessUnit - CategoryCode) */
	IF((SELECT COUNT(1) FROM #TMP_TABLE) > 0)
	BEGIN
		IF(@AddressBookType = 0) /* CUSTOMER */
