		ELSE IF (@AddressBookType = 1) /* CONSUMER */
		BEGIN
			SELECT
				@PricingGroup = PRCPGP,
				@AdjustmentSchedule = PRASN,
				@State = PRADDS,
				@Country = PRCTR,
				@BusinessUnit = PRMCU,
				@CategoryCode01 = PR$9AC01,
				@CategoryCode02 = PR$9AC02,
