	LEFT JOIN [SCDATA].FQ67102L HHLNGHED
         ON HHLNGHED.HH$9HHA = HHHED.HH$9HHA
         AND HHLNGHED.HHLNGP = @LangPref
    WHERE (@AboutUsID = '*' OR HHHED.HH$9HHA LIKE '%' +  @AboutUsID + '%')
