CREATE Procedure [DBO].SEC_ExcSyncWebAcctPassword
(
	@WebUserName		NVARCHAR(256),
	@StoreListXML		XML,
	@Password NVARCHAR(256),
	@LastDatePasswordChanged  NUMERIC
)	
AS

