	INNER JOIN @StoreListXML.nodes('/stores/storeID') AS stores(storeID)
				ON stores.storeID.value('.','NVARCHAR(3)') = INBU.UI$9INID
WHERE
	WUSR.WAEMAL = @WebUserName;
