			ISNULL((SELECT TOP 1 EAEMAL
						FROM	[SCDATA].F01151
						WHERE	EAAN8 = A.AddressNumber
						AND		EAIDLN = A.ContactID
						AND		EAETP = 'E'
						ORDER BY (CASE WHEN EAEHIER = 1 THEN 0 ELSE 1 END) ASC, EARCK7 DESC),'') AS Email,
			ISNULL((SELECT TOP 1 EAEMAL
