AS

	INSERT INTO [SCDATA].FQ67302
	(
		 IXKIT, IXMMCU, IX$9AN8, 
		 IX$9MMID, IXITM, IXCPNT, IXQNTY, 
		 IX$9MQTY, IXUM, IXEFFF, IXEFFT, 
		 IX$9OPTK, IXLNTY, IXPRIC, IX$9MPRC, 
		 IXURCD, IXURDT, IXURAT, IXURRF, 
		 IXURAB, IXUSER, IXPID, IXJOBN, 
		 IXUPMJ, IXTDAY
	)
