CREATE  PROCEDURE [DBO].CLB_ExcCheckShipToForJoinClubs
	@InstallationID NVARCHAR(3),
	@ShipToId		FLOAT, 
	@AddressType	FLOAT
	
AS	
	DECLARE @State  NVARCHAR(10)	
	DECLARE @CurrentJulianDate NUMERIC = [DBO].CMM_GetCurrentJulianDate(GETDATE());
	
