			CASE WHEN C.RFOGNO IS NULL THEN 'N' ELSE 'Y' END AS IsFreeGood,
			IsBasketRepricing,
			ISNULL(C.RFOGNO, 0) AS ParentLineNo
		FROM #TMP_ORDERDETAIL A 
		LEFT OUTER JOIN [SCDATA].F4573 C  /* Free Goods lines */
