		OR (A.SDPSN = 0 AND A.SDCNID = '' AND A.SDNXTR = @NextStatus AND A.SDLNTY IN (@FreightLn, @TrackingLn) AND (CASE @AddressType WHEN 1 THEN B.SD$9SHAN ELSE A.SDSHAN END) = @SDSHAN AND A.SDMCU = @SDMCU) 		
