					AND B.SD$9TYP = 1
				' + @WHERE_DYNAMIC + N'
			) ';
		END	
	END
	ELSE BEGIN
		SET @UNION_DYNAMIC = N' UNION_LINES AS (
			SELECT 	
				A.SDDOCO	AS OrderNumber, 
				A.SDDCTO	AS OrderType, 
				A.SDKCOO	AS Company,    
