			DRUSER,
			DRUPMJ,
			DRUPMT
		)
		VALUES
		(
			@RegisterID,
			@DrawerNumber,
			@BatchNumber,
			@SequenceNumber,
			@LineNumber,
			@PaymentInstrument,
			@CurrencyCode,
			@AmountTransact,
			@UserId,
			'', /*Type Transaction*/
