	(CASE A.RC$9DAC WHEN 0 THEN @currentDate ELSE A.RC$9DAC END) AS RegDrawerDate,
	(CASE A.RC$9TIC WHEN 0 THEN @currentTime ELSE A.RC$9TIC END) AS RegDrawerTime
FROM
	[SCDATA].FQ67427 A
