-- #desc				Get Sales Order Detail Information
-- #bl_class			N/A
-- #db_dependencies		N/A
-- #db_references		N/A			

CREATE OR REPLACE VIEW [SCLIBRARY].COM_OrderDetailVW
AS
WITH CTE_F4211 AS (
	SELECT 
		A.SDKCOO,
		A.SDDOCO,
		A.SDDCTO,
		A.SDLNID,
		A.SDSHAN AS SHAN,
		A.SDITM,
		A.SDNXTR,
		A.SDLNTY,
		A.SDLTTR,
		A.SDCNDJ,
		A.SDRLIT
	FROM [SCDATA].F4211 A		--Sales Order Lines
	UNION ALL
	SELECT 
		A.SDKCOO,
		A.SDDOCO,
		A.SDDCTO,
		A.SDLNID,
		A.SDSHAN AS SHAN,
		A.SDITM,
		A.SDNXTR,
		A.SDLNTY,
		A.SDLTTR,
		A.SDCNDJ,
		A.SDRLIT
	FROM [SCDATA].F42119 A	
	)--Historical Sales Order Lines

	SELECT 
		A.SDKCOO,
		A.SDDOCO,
		A.SDDCTO,
		A.SDLNID,
		CASE SD$9TYP WHEN 1 THEN B.SD$9SHAN ELSE A.SHAN END AS SHAN,
		A.SDITM,
		A.SDNXTR,
		A.SDLNTY,
		A.SDLTTR,
		A.SDCNDJ,
		A.SDRLIT
	FROM CTE_F4211 A
	LEFT OUTER JOIN [SCDATA].FQ674211 B	--Extended Sales Order Lines
		ON A.SDKCOO = B.SDKCOO 
		AND A.SDDOCO = B.SDDOCO 
		AND A.SDDCTO = B.SDDCTO 
		AND A.SDLNID = B.SDLNID;
/

