	    SELECT AICO INTO CompanyTMP FROM [SCDATA].F03012
						WHERE AIAN8 = AddressNumber AND AICO = Company AND ROWNUM < 2;
	    EXCEPTION WHEN NO_DATA_FOUND THEN 
	    CompanyTMP := NULL;
	END;
	
	IF(CompanyTMP IS NULL)THEN
		CompanyTMP := '00000';
