			ORDER BY (CASE WHEN EAEHIER = 1 THEN 0 ELSE 1 END) ASC, EARCK7 DESC	    
		)TMP
		WHERE ROWNUM < 2;
		EXCEPTION WHEN NO_DATA_FOUND THEN 
		    ElectronicAddress := NULL;
	END;
	
	RETURN NVL(ElectronicAddress,'');
