	DELETE FROM [SCDATA].FQ67997 WHERE EXISTS(SELECT US$9USER 
	                                           FROM [SCDATA].FQ67991   US,
													[SCDATA].FQ67101   WA,
													[SCDATA].FQ67996	UI	
