	                                           FROM [SCDATA].FQ67101 WA,
													[SCDATA].FQ67996 UI
													WHERE 
													        WA.WA$9USER = UI.UI$9USER --INNER JOIN
													    AND WA.WA$9WAN8 = WD$9WAN8
														AND UI.UI$9INID = InstallationID);
	
