	ResultData1 OUT GLOBALPKG.refcursor
)
AS
	CurrentJulianDate 	NUMBER(6,0) := [SCLIBRARY].CMM_GetCurrentJulianDate (SYSDATE);
BEGIN
	OPEN ResultData1 FOR
	SELECT
		A.MCAN8 AS AddressNumber,
