	INNER JOIN TABLE(SCLIBRARY.CMM_GetEffectiveAddressFnc (A.MCAN8, CurrentJulianDate)) D	/*Address*/
		ON D.ALAN8 = A.MCAN8
	WHERE 
		B.BI$9INID = InstallationID
		AND A.MCMCU = BranchPlant
		AND B.BILCNF = 'Y';
		
